import React from "react"
import { NavLink } from 'react-router-dom'

import { useState } from "react"

import { useLocation } from 'react-router-dom';

const location = useLocation()


const CreateComment = () => {
    const [comment, createComment] = useState("")

    const setComment = (e) => {
        createComment(() => e.target.value)
    }

    const id  = location.state.id
    const comments = location.state.comments

    return (
        <div>
            <p>Comment</p>
            <NavLink 
                className="back-to-home-page-from-comments" 
                to='/achievements/comments'
                state={{ id: id,
                    comments: comments
                   }}        
                >
                    Back        
            </NavLink>
            <div>
                <textarea
                    placeholder="comment"
                    onChange={setComment}
                />
                <p>{comment}</p>
            </div>
        </div>
    )
}

export default CreateComment
