import React from 'react'
import { useState } from 'react'

import lowfi from "../gif/lowfi.gif";
import night from "../gif/night.gif";
import sunset from "../gif/sunset.gif";
import train from "../gif/train.gif";

const Music = () => {

    const gif = [lowfi, night, sunset, train]

    const [gifState, setGifState] = useState(gif[0])

    const handleGifState = () => {
        if (gifState != gif[3]) {
            setGifState(() => gif[1])

        }

        console.log(gifState)
    }

    return (
        <div className="music-container">
        <img
            className="music-bg"
            src={gifState}
        />
        <iframe
            className="audio-bar"
            src="https://audio.jukehost.co.uk/WtwVEwikT3CkVjbxrmgmJQozQGIGxwNh"
            frameBorder="0" scrolling="no"
       />
       <button className="next-button-gif" onClick={handleGifState}>
            Next Gif
       </button>
       <button className="next-button-music">
            Next Song
       </button>
       </div>
    )
}

export default Music
