import React from 'react'
import { Component } from "react";
import Countdown from "./Countdown";
import Title from "../Title/Title.js";
import Header from '../Header/Header.js'
import studying from '../img/night.gif'
import './FocusSession.css'

import { TextField } from '@mui/material';


function Timer () {
     var iframes = document.getElementsByTagName("iframe");

    for (let i = 0; i < iframes.length; i++) {
        var url = iframes[i].getAttribute("src");
        if (url.startsWith("https://docs.google.com/document/d/")) {
            // create div and replace iframe
            let d = document.createElement('div');
            d.classList.add("embedded-doc"); // optional
            iframes[i].parentElement.replaceChild(d, iframes[i]);

            // CORS request
            var xhr = new XMLHttpRequest();
            xhr.open('GET', url, true);
            xhr.onload = function() {
                // display response
                d.innerHTML = xhr.responseText;
            };
            xhr.send();
        }
    }
    return (
        <div class="timer">
            <Header />
            <Title name={'Focus Session'} />
            <div className="timer-music-container">
                <div className="timer-container">
                    <div className="timer-input-container">
                        <TextField 
                            id="standard-basic" 
                            label="Hours" 
                            variant="standard" 
                            className="add-time-input"
                            placeholder={"Hours"}
                        />
                        <TextField 
                            id="standard-basic" 
                            label="Mins" 
                            variant="standard" 
                            className="add-time-input"
                            placeholder={"Mins"}
                        />
                        <TextField 
                            id="standard-basic" 
                            label="Secs" 
                            variant="standard" 
                            className="add-time-input"
                            placeholder={"Mins"}
                        />
                    </div> 
                    <button className="start-timer-button">
                        Start
                    </button>  
                    <Countdown 
                        className={"countdown"}
                    />
                </div>              
                <div className="music-container">
                    <img src={studying}></img>
                    <iframe 
                        src="https://docs.google.com/uc?export=open&id=1tRBKzfsEcVJF5bb00xY4xRTuUN01S8aZ" 
                        className="audio-bar"
                        frameBorder="0"
                        height="100"
                        width="500"
                    />           
                </div>
            </div>
        </div>
    )
}

export default Timer;
