import React from 'react'
import Title from '../Title/Title.js'
import Header from '../Header/Header.js'
import './ActivityHeatmap.css'

import { useState, useEffect } from 'react'
import { rangeContainsMarker } from '@fullcalendar/react'

let keys = [...Array(365).keys()]
let values = Array(365).fill("#ddd")
let arr = {}
for (let key of keys) {
    arr[key] = "#ddd"
}
keys.forEach((key) => arr[key] = "#ddd")
console.log(arr)

const ActivityHeatmap = () => {

    const [colour, setColour] = useState(keys)

    const handleClick = (e) => {
        let elemId = e.target.id
        let elem = document.getElementById(elemId)

        if (elem.style.backgroundColor != "orange") {
            elem.style.backgroundColor = "orange"
        } else {
            elem.style.backgroundColor = "#ddd"
        }

        console.log(colour)
    }


    return (
        <div class="squaregrid">
            {keys.map((item) => <button className={"cell"} onClick={handleClick} id={item}  
            style={{backgroundColor: "#ddd"}}/>)}
        </div>
    )
}

export default ActivityHeatmap;
