import React from "react"
import { NavLink } from 'react-router-dom'

import { useState } from "react"



const CreateComment = () => {
    const [comment, createComment] = useState("")

    const setComment = (e) => {
        createComment(() => e.target.value)
    }

    return (
        <div>
            <div>
                <textarea
                    placeholder="comment"
                    onChange={setComment}
                />
                <button>
                    Submit comment
                </button>
            </div>
        </div>
    )
}

export default CreateComment
