import Card from './Card.js';
import UserTasks from './UserTasks.js';

function Todolist () {
    // To-do list Cards
    const userTasks = UserTasks.map(item => {
        return <Card
                    task = {item.task}
                    day = {item.day}
                    urgency = {item.urgency}
                />
    });

    return (
