            <form>
                <input className="todoinput"></input>
                <button className="todobutton">Add New Task</button>
            </form>
        </div>

        <div className="cards">
            {userTasks}
        </div>

        </>*/
        <form> 
        <input 
            type="text"
            placeholder="Enter a task"
            className = "task-input"
            value = {taskName}
            required
            onChange={handleChange}
        />
        <button></button>
    </form>
    );
}

export default Todolist;
