import React from "react"; 
import ActiveJournalEntriesBody from './ActiveJournalEntriesBody.js'
import { useState } from "react";

const ActiveJournalEntries = (props) => {
    const [showContent, setShowContent] = useState(false)

    const showJournalContent = () => {
        setShowContent(() => !showContent)
    }

    return (
        <div className="archived-entry-container">
            <button onClick={showJournalContent}>{props.title}</button>
            <div>
            {showContent &&  
            <ActiveJournalEntriesBody
                title={props.title}
                body={props.body}
                conclusion={props.conclusion}
                id={props.id}
            /> 
            }

            </div>

        </div>
    )

}

<div className="archived-entry-container">
<button onClick={toggleEntry} className="archived-entries-conclusion">{props.conclusion}</button>
{clicked && <p className="archived-entries-text">{props.title}</p>}
{clicked && <p className="archived-entries-text">{props.body}</p>}
</div>

export default ActiveJournalEntries
