import { useState } from "react";

import CheckCircleIcon from "@mui/icons-material/CheckCircle";
import EditIcon from "@mui/icons-material/Edit";
import DeleteIcon from "@mui/icons-material/Delete";

import {db} from "../../firebase.js"

import {
  collection,
  query,
  onSnapshot,
  doc,
  updateDoc,
  deleteDoc,
  QuerySnapshot
} from "firebase/firestore"

const Todoitem = (props) => {

  const [input, setNewInput] = useState("")

  const handleDelete = async (id) => {
    await deleteDoc(doc(db, "todos", id));
  };

  const handleEdit = async (todo, title) => {
    await updateDoc(doc(db, "todos", todo.id), { title: title });
  };

  const handleChangeInInputField = (e) => {
    setNewInput(() => e.target.value)
  }

  return (
    <div className="task-container">
      <form className="tasks">
        <div className="input-container">
          <input 
            className="todo-item"
            type="text"
            value={todo.title === "" ? newTitle : todo.title}
            placeholder="task"
            onChange={handleChangeInInputField}
          />
        </div>
        <div className="buttons-container">
          <button className="Delete" onClick={() => handleDelete(props.id)}>
            Delete
          </button>
          <button className="Edit" onClick={() => handleEdit(props, props.task)}>
            Edit
          </button>
        </div>
      </form>
    </div>
  )
}

export default Todoitem
