import React, { useState, useRef } from 'react';
import prepop from '../img/prepop.svg'
import pop from '../img/popped.svg'

import './Analytics.css'

const Analytics = () => {

    let arr = [...Array(200).keys()]

    let bubbleStateArr = Array(200).fill(prepop)

    const[bubbleState, setBubbleState] = useState(prepop)

    const popBubble = (e) => {
        let id = e.target.id
        setBubbleState(() => pop)
    }

    return (
        <div className="bubble-wrap-container">
            <h1 >Bubble Wrap</h1>
            {arr.map((index) => (
                <img 
                className="bubbles" 
                id={index}
                onClick={popBubble}
                src={bubbleState}
                />
            ))}
        </div>
    )

}
export default Analytics;
