import React from "react";
import { useState, useRef } from 'react';
import prepop from '../img/prepop.svg'
import pop from '../img/popped.svg'

import './Analytics.css'

const Bubble = (props) => {

    const[bubbleState, setBubbleState] = useState(prepop)

    const popBubble = () => {
        setBubbleState(() => pop)
    }

    return (
        <img 
            className="bubbles" 
            id={props.index}
            onClick={popBubble}
            src={bubbleState}
        />
    )

}


function App() {
  let audio = new Audio("../sound-effects/christmas.mp3")

  const start = () => {
    audio.play()
  }

  return (
    < div >
      <button onClick={start}>Play</button>
    </div >
  );
}

export default App;
