import React from 'react'
import { useState } from 'react'

import lowfi from "../gif/lowfi.gif";
import night from "../gif/night.gif";
import sunset from "../gif/sunset.gif";
import train from "../gif/train.gif";
import cat from "../gif/cat.gif";


const Music = () => {

    const gif = [lowfi, night, sunset, train, cat]
    const [gifState, setGifState] = useState(0)
    const showNextGif = () => {
        if (gifState <= gif.length) {
            setGifState(() => gifState + 1)
        } else {
            setGifState(() => 0)
        }
    }



    return (
        <div className="music-container">
            <img
                className="music-bg"
                src={gif[gifState]}
            />
            <iframe
                className="audio-bar"
                src="https://audio.jukehost.co.uk/WtwVEwikT3CkVjbxrmgmJQozQGIGxwNh"
                frameBorder="0" scrolling="no"
        />
        <button className="next-button-gif" onClick={showNextGif}>
                Next Gif
        </button>
        <button className="next-button-music">
                Next Song
        </button>
       </div>
    )
}

export default Music
