import React from 'react'
import { useState, useEffect } from 'react'
import { NavLink } from 'react-router-dom'

import { db } from "../../firebase"

import { UserAuth } from '../../context/AuthContext'

import {
    collection,
    query,
    onSnapshot,
    doc,
    updateDoc,
    deleteDoc,
    QuerySnapshot,
    orderBy, 
    where
    } from "firebase/firestore"
import { PropaneSharp } from '@mui/icons-material'

import { useLocation } from 'react-router-dom';

const location = useLocation()

const id  = location.state.id
const comments = location.state.comments


const Comments = () => {

    const {user}  = UserAuth()

    useEffect(() => {
        let active = true
        if (active == true && user.uid != null) {
          const q = query(collection(db, "posts"))
          console.log("Retrieving task list")
          const getAllTasks = onSnapshot(q, (querySnapshot) => {
          let tasks = []
          querySnapshot.forEach((doc) => {
            tasks.push({...doc.data(), id:doc.id})
            console.log(doc.id)
            console.log("hello")
          })
          console.log(tasks)

        })
        return () => {active = false}}
      },[user.uid])

      const location = useLocation()

      const id  = location.state.id
      const comments = location.state.comments

    return (
        <div>
            <p>Hello</p>
            <NavLink className="back-to-home-page-from-comments" to='/achievements'>
                    Back        
            </NavLink>
            <NavLink 
                className="link-to-create-new-comment" 
                to='/achievements/createcomment'
                state={{ id: id,
                         comments: comments
                        }}
                >
                    Create New Comment        
            </NavLink>
            <p>{id}</p>
            <p>{comments}</p>
        </div>
    )

}

export default Comments;
