import React from 'react'

import { UserAuth } from '../../context/AuthContext'


import {db} from "../../firebase.js"
import {
    collection,
    query,
    onSnapshot,
    doc,
    updateDoc,
    deleteDoc,
    QuerySnapshot,
    orderBy, 
    where
  } from "firebase/firestore"

import { NavLink } from 'react-router-dom'
const [currentTasks, setCurrentTasks] = useState([])
  useEffect(() => {
    let active = true
    if (active == true && user.uid != null) {
      const q = query(collection(db, "todos"), where ("uid", "==", user.uid), orderBy('createdAt'))
      console.log("Retrieving task list")
      const getAllTasks = onSnapshot(q, (querySnapshot) => {
      let tasks = []
      querySnapshot.forEach((doc) => {
        tasks.push({...doc.data(), id:doc.id})
        console.log(doc.data())
      })
      setCurrentTasks(() => tasks)
      console.log(currentTasks)

    })
    return () => {active = false}}
  },[user.uid])

const ArchiveJournalEntries = () => {
    const {user}  = UserAuth()

    return (
        <div>
            hello
        </div>
    )

}

export default ArchiveJournalEntries
