    const [todos, setTodos] = useState([])

    useEffect((e) => {
        let ignore = false
        let q = query(collection(db, "todos"))
        let getAllTasks = onSnapshot(q, (querySnapshot) => {
            let newTasks = []
            querySnapshot.forEach((doc) => {
                newTasks.push({...doc.data(), id: doc.id})
            })
            setTodos(() => newTasks)
        })
        console.log(todos)
        return () => { ignore = true }
    },[])



    return (
        <div className='calendar'>
            <Addtodo />
            <button>Hello</button>
        </div> 
    )
}




export default Calendar;
