import Title from '../Title.js'
import AddTodo from './AddTodo.js'
import Todoitem from './Todoitem.js'

import React from "react"
import { useState, useEffect } from "react"

import ElderlyWomanIcon from '@mui/icons-material/ElderlyWoman';

import {db} from "../../firebase.js"
import {
    collection,
    query,
    onSnapshot,
    doc,
    updateDoc,
    deleteDoc,
    QuerySnapshot,
    orderBy
  } from "firebase/firestore"


const Todolist =  () => {
  const [currentTasks, setCurrentTasks] = useState([])

  useEffect(() => {
    let active = true

    if (active == true) {
      const q = query(collection(db, "todos"), orderBy('createdAt'))
      const getAllTasks = onSnapshot(q, (querySnapshot) => {
      let tasks = []
      querySnapshot.forEach((doc) => {
        tasks.push({...doc.data(), id:doc.id})
      })
      setCurrentTasks(() => tasks)
      console.log(tasks)
    })}
    return () => {active = false}
  },[])

  const[poop, setPoop] = useState(0)

  const logPoop = () => {
    setPoop(() => poop + 1)
  }

  const resetPoop = () => {
    setPoop(() => 0)
  }

  return (
    <div className="todo-list-main">
      <Title name={"Todo List"}/>
      <AddTodo />
      {currentTasks.map((todo) => (
        <Todoitem 
          item={todo}
        />
      ))}
      <br></br>
      <br></br>
      <br></br>
      <br></br>
      <br></br>
      <br></br>
      <p>How many times yong en poopied todai?</p>
      <button onClick={logPoop}>
        <ElderlyWomanIcon />
        </button>
      <button onClick={resetPoop}>
        Just keedin
      </button>
      <p>Poop counter: {poop}</p>

    </div>
  )
}

export default Todolist
