import React from 'react'
import Title from '../Title/Title.js'
import Header from '../Header/Header.js'
import './ActivityHeatmap.css'

import { useState } from 'react'
import { rangeContainsMarker } from '@fullcalendar/react'

const ActivityHeatmap = () => {

    const [colour, setColour] = useState("cell")

    const handleClick = (e) => {
        setColour(() => colour == "cell" ? "cell-clicked" : "cell")
    }

    let arr = [...Array(365).keys()]


    return (
        <div class="squaregrid">
            {arr.map((item) => <button className={colour} onClick={handleClick} id={item} />)}
        </div>
    )
}

export default ActivityHeatmap;
