import React from "react"
import { useState, useEffect } from "react"

import CheckCircleIcon from "@mui/icons-material/CheckCircle"
import EditIcon from "@mui/icons-material/Edit"
import DeleteIcon from "@mui/icons-material/Delete"

import {db} from "../../firebase.js"
import {
    collection,
    query,
    onSnapshot,
    doc,
    updateDoc,
    deleteDoc,
    QuerySnapshot
  } from "firebase/firestore"

const Todoitem = (props)  => {

  const { name, completed, id } = props;

  const [currentTask, setCurrentTask] = useState("")

  const handleDelete = async (id) => {
    await deleteDoc(doc(db, "todos", id))
  }

  const handleEdit = async (id, task) => {
    await updateDoc(doc(db, "todos", id), {task: task})
  }

  const handleCurrentTaskChange = (e) => {
    setCurrentTask(() => e.target.value)
  }

  return (
    <div className="todo-item">
      <input
        className={"todo-item-input-field"}
        value={currentTask == "" ? props.item.task : currentTask}
        onChange={handleCurrentTaskChange}
      />
      <button className="todo-item-delete-button" onClick={() => handleDelete(id)}>
        Delete
      </button>
      <button className="todo-item-edit-button" onClick={() => handleEdit(id, currentTask)}>
        Save Edit
      </button>
    </div>

  )
}

export default Todoitem 


