        let ignore = false
        const q = query(collection(db, "todos"));
        const unsub = onSnapshot(q, (querySnapshot) => {
          let todosArray = [];
          querySnapshot.forEach((doc) => {
            todosArray.push({ ...doc.data(), id: doc.id});
          });
          setTodos(() => todosArray);
        });
        console.log(todos)
        return () => { ignore = true };
      }, []);

    return (
        <div className='calendar'>
