import React from "react"; 

import { useState } from "react";
const CanvasTimer = () => {
    let [timerClock, setTimerClock] = useState(5000); 
    useEffect(() => {
        const timer = setTimeout(() => {
            console.log("minus: ", timerClock)
            setTimerClock(() => timerClock - 1);
        }, 1000)
        return () => { 
        clearTimeout(timer)
        }
    }, [timerClock]);


    return (
        <div>
            <p>{timerClock}</p>
        </div>
    )
}


export default CanvasTimer
