import React, { Component } from "react";
import "../App.css";

class Timer extends Component {
    state = {
        onTimer: false, // Is the timer on?
        startTime: 0, // Start time (in milliseconds).
        totalTime: 0 // Total time (in ms) since start or reset.
    };

    // Called when timer is started or resumed.
    // Start the timer by turning on the timer and setting current time as startTime and totalTime.
    startTimer = () => {
        this.setState({
          onTimer: true,
          startTime: this.state.timerTime,
          totalTime: this.state.timerTime
        });

        this.timer = setInterval(() => {
            const remainingTime = this.state.timerTime - 10;
            if (remainingTime >= 0) {
                // Remaining time is more than zero,
                // so update total time accordingly.
                this.setState({
                    totalTime: newTime
                });
            } else {
                // Remaining time is less than zero.
                clearInterval(this.timer);
                this.setState({ timerOn: false });
                alert("Countdown ended");
            }
        }, 10);
    };

    render() {
        return (
            <div className="Timer">
                <h1 class="title">Timer</h1>
            </div>
        );
    }
}

export default Timer;
